import React, { useState } from 'react';
import Slider from 'react-slick';
import { Icon } from '@iconify/react';
import Testimonial from '../Testimonial';
import Div from '../Div';
import Spacing from '../Spacing';
export default function TestimonialSlider() {
  const [nav1, setNav1] = useState();
  const [nav2, setNav2] = useState();
  const testimonialData = [
    {
      testimonialThumb: '/images/testimonial-01.jpg',
      testimonialText:
        'Working with DH Solutions has been a game-changer for our business! Their innovative approach and expert team turned our vision into a remarkable reality. Thanks to their exceptional website design and digital strategies, our online presence has soared, and our brands impact has grown significantly.',
      avatarName: 'Jane Smith',
      avatarDesignation: 'CEO of TechConnect Solutions',
      ratings: '4',
    },
    {
      testimonialThumb: '/images/testimonial-02.jpeg',
      testimonialText:
        'DH Solutions is the epitome of professionalism and excellence. Their dedicated IT experts seamlessly integrated our complex software systems, streamlining our operations like never before. Their commitment to quality and timely delivery is truly commendable, and we are proud to have them as our digital partners.',
      avatarName: 'John Doe',
      avatarDesignation: 'CTO of Global Logistics Inc',
      ratings: '5',
    },
    {
      testimonialThumb: '/images/testimonial-03.jpeg',
      testimonialText:
        'I can not thank DH Solutions enough for the outstanding mobile app they developed for my startup. Their teams attention to detail, user-centric design, and flawless execution exceeded my expectations. The app has garnered rave reviews from our users, and it,s all thanks to the DH Solutions teams expertise and dedication.',
      avatarName: 'Emily Rodriguez',
      avatarDesignation: 'Founder of FitnessFusion App',
      ratings: '4.5',
    },
    {
      testimonialThumb: '/images/testimonial-04.jpeg',
      testimonialText:
        'DH Solutions turned our digital dreams into a dazzling reality! Their creative brilliance and technical expertise transformed our website into a captivating online experience. The seamless collaboration and unwavering support from their team made the entire process a breeze. DH Solutions truly understands our brand and delivered beyond our expectations.',
      avatarName: 'Mark Thompson',
      avatarDesignation: 'Marketing Director at StellarGems Jewelry',
      ratings: '3.5',
    },
  ];
  const SlickArrowLeft = ({ currentSlide, slideCount, ...props }) => (
    <div
      {...props}
      className={
        'slick-prev slick-arrow' + (currentSlide === 0 ? ' slick-disabled' : '')
      }
      aria-hidden="true"
      aria-disabled={currentSlide === 0 ? true : false}
    >
      <Icon icon="bi:arrow-left" />
    </div>
  );
  const SlickArrowRight = ({ currentSlide, slideCount, ...props }) => (
    <div
      {...props}
      className={
        'slick-next slick-arrow' +
        (currentSlide === slideCount - 1 ? ' slick-disabled' : '')
      }
      aria-hidden="true"
      aria-disabled={currentSlide === slideCount - 1 ? true : false}
    >
      <Icon icon="bi:arrow-right" />
    </div>
  );
  return (
    <>
      <Div className="cs-gradient_bg_1 cs-shape_wrap_3 cs-parallax">
        <Spacing lg="130" md="80" />
        <Div className="cs-shape_3 cs-to_up">
          <img src="/images/shape_1.svg" alt="Shape" />
        </Div>
        <Div className="container">
          <Div className="cs-testimonial_slider">
            <Div className="cs-testimonial_slider_left">
              <Slider
                asNavFor={nav1}
                ref={slider2 => setNav2(slider2)}
                slidesToShow={3}
                swipeToSlide={true}
                focusOnSelect={true}
                centerMode={true}
                centerPadding="0px"
                arrows={false}
              >
                {testimonialData.map((item, index) => (
                  <Div className="slider-nav_item" key={index}>
                    <Div className="cs-rotate_img">
                      <Div className="cs-rotate_img_in">
                        <img src={item.testimonialThumb} alt="Thumb" />
                      </Div>
                    </Div>
                  </Div>
                ))}
              </Slider>
            </Div>
            <Div className="cs-testimonial_slider_right">
              <Slider
                asNavFor={nav2}
                ref={slider1 => setNav1(slider1)}
                prevArrow={<SlickArrowLeft />}
                nextArrow={<SlickArrowRight />}
                className="cs-arrow_style1"
              >
                {testimonialData.map((item, index) => (
                  <Div key={index}>
                    <Testimonial
                      testimonialText={item.testimonialText}
                      avatarName={item.avatarName}
                      avatarDesignation={item.avatarDesignation}
                      ratings={item.ratings}
                    />
                  </Div>
                ))}
              </Slider>
            </Div>
          </Div>
        </Div>
        <Spacing lg="130" md="80" />
      </Div>
    </>
  );
}
